<?phprequire_once('base/Model.php');require_once('base/Db.php');class TableModel extends Model{	public function tableName()    {        return '';    }        public function findAll($condition = '', $order = '')    {        $resultArray = Db::getInstance()->select($this->tableName(), '*', $condition, $order);        $result = array();        $className = get_class($this);                    foreach ($resultArray as $row)        {            $newObject = new $className;            $newObject->setAttributes($row);            $result[] = $newObject;        }        return $result;    }		public function save($validate = true)    {        if ($validate && !$this->validate())            return false;        else        {            return Db::getInstance()->insert($this->tableName(), $this->_attributes);        }            }	}