<?php/** * Translates message to current language. * @param string $category message category * @param string $message message to translate */function tr($category, $message){    $lang = isset($_GET['lang'])? $_GET['lang']: 'en';    $messageFile = 'protected/messages/'.$lang.'/'.$category.'.php';        return (file_exists($messageFile) && ($messages = include($messageFile)) && isset($messages[$message])? $messages[$message]: $message);}