<?php/** * Translates message to current language. * @param string $category message category * @param string $message message to translate */function tr($category, $message){    $lang = isset($_GET['lang'])? $_GET['lang']: 'en';    $messageFile = 'protected/messages/'.$lang.'/'.$category.'.php';    if (file_exists($messageFile))        $messages = include($messageFile);    else        throw new Exception("Message file for language '$lang' not found");    return (isset($messages[$message])? $messages[$message]: $message);}