<?phprequire_once('base/Controller.php');require_once('protected/models/Configuration.php');class ConfigurationController extends Controller{	public function actionSetup()	{		$config = new Configuration('protected/config/main.php');				if (isset($_POST['Configuration']))		{			$config->setAttributes($_POST['Configuration']);			if ($config->save())			{				header('Location: /guestbook.php');			}		}				$this->render('_form', array('configuration'=>$config));	}}