<?phpclass Comment{        private $_attributes = array(        'author'=>'',        'createTime'=>'',        'email'=>'',        'content'=>'',    );                   public static function findAll()    {        $result = array();        mysql_connect('localhost', 'root', '');        mysql_select_db('dealerfire_test');        $q = mysql_query('SELECT * FROM tbl_comment ORDER BY createTime');        while ($row = mysql_fetch_assoc($q))        {            $comment = new Comment();            $comment->setAttributes($row);            $result[] = $comment;        }        return $result;    }            public function setAttributes($attributes)    {        foreach ($attributes as $name => $value)        {            if (isset($this->_attributes[$name]))                $this->_attributes[$name] = $value;        }    }        public function getAttribute($name)    {        if (isset($this->_attributes[$name]))            return $this->_attributes[$name];        else            throw new Exception("Attribute $name doesn\'t exist in the model {__CLASS__}");    }}